import React from 'react';

const About = ({ setCurrentPage }) => {
  const handleLinkClick = (page) => {
    setCurrentPage(page);
  };

  return (
      <header>
        <h1>About me</h1>
      <nav>
        <ul>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('home')}
            >
              Home
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('about')}
            >
              About
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('projects')}
            >
              Projects
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('contact')}
            >
              Contact
            </button>
          </li>
        </ul>
      </nav>

      <div style={bioBoxStyle}>
        <p>
          This is my personal website where I post what I'm up to and all about me!
        </p>
        <img
          src={require('./images/me.png')}
          alt="Me"
          style={bioimage}
        />
      </div>

      <div style={imageContainerStyle}>
        <img
          src={require('./images/psu.png')}
          alt="Psu"
          style={imageStyle}
        />
        <img
          src={require('./images/fightins.png')}
          alt="Phillies"
          style={imageStyle}
        />
        <img
          src={require('./images/eagles.png')}
          alt="Go birds"
          style={imageStyle}
        />
        <img
          src={require('./images/sixers.png')}
          alt="76ers"
          style={imageStyle}
        />
        <img
          src={require('./images/flyers.png')}
          alt="Flyers"
          style={imageStyle}
        />
      </div>

        
      </header>
  );
};

const buttonStyle = {
  padding: '10px 50px',
  backgroundColor: '#556',
  color: '#fff',
  border: 'none',
  borderRadius: '10px',
  cursor: 'pointer',
};

const imageContainerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '100px'
};

const bioimage = {
  width: '15%',
  height: '100%',
  objectFit: 'cover',
  border: '7px solid #332',
  boxShadow: '20px 10px 5px rgba(0, 0, 0, 0.3)',
};

const imageStyle = {
  width: '15%',
  height: '100%',
  objectFit: 'cover',
};


const bioBoxStyle = {
  marginTop: '100px',
  backgroundColor: '#222',
  textAlign: 'center',
  padding: '20px'
};
export default About;
