import React from 'react';

const Projects = ({ setCurrentPage }) => {
    const handleLinkClick = (page) => {
      setCurrentPage(page);
    };
  return (
    <header>
      <h1>Projects</h1>
      <nav>
        <ul>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('home')}
            >
              Home
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('about')}
            >
              About
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('projects')}
            >
              Projects
            </button>
          </li>
          <li>
            <button
              style={buttonStyle}
              onClick={() => handleLinkClick('contact')}
            >
              Contact
            </button>
          </li>
        </ul>
      </nav>
      <div style={projectContainerStyle}>
        <div style={projectBoxStyle}>
          <img src="project1.jpg" alt="Project 1" style={imageStyle} />
          <div style={descriptionStyle}>
            <h2>Project 1</h2>
            <p>Description of Project 1</p>
          </div>
        </div>
      </div>
    </header>

  );
};

const buttonStyle = {
  padding: '10px 50px',
  backgroundColor: '#556',
  color: '#fff',
  border: 'none',
  borderRadius: '10px',
  cursor: 'pointer',
};

const projectContainerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  marginTop: '100px',
  backgroundColor: '#222',
  };
  
  const projectBoxStyle = {
  display: 'flex',
  alignItems: 'center',
  marginBottom: '20px',
  };
  
  const imageStyle = {
  width: '200px',
  height: '200px',
  };
  
  const descriptionStyle = {
  marginLeft: '20px',
  };

export default Projects;
