import React, {useState} from 'react';

const Certifications = ({ setCurrentPage }) => {
  const [hoveredButton, setHoveredButton] = useState(null);

  const handleLinkClick = (page) => {
    setCurrentPage(page);
  };

  const handleButtonMouseEnter = (buttonName) => {
    setHoveredButton(buttonName);
  };

  const handleButtonMouseLeave = () => {
    setHoveredButton(null);
  };

  const getButtonStyle = (buttonName) => {
    const baseButtonStyle = {
      padding: '20px 50px',
      backgroundColor: '#556',
      color: '#fff',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      marginRight: '20px',
    };

    if (hoveredButton === buttonName) {
      return {
        ...baseButtonStyle,
        boxShadow: '0 0 5px white, 0 0 25px white, 0 0 50px white, 0 0 100px white, 0 0 200px white',
      };
    }

    return baseButtonStyle;
  };

  return (
    <header>
      <div style={headerStyle}>
        <h1>Certifications</h1>
        <nav>
          <ul style={buttonContainerStyle}>
            <li>
              <button
                style={getButtonStyle('home')}
                onMouseEnter={() => handleButtonMouseEnter('home')}
                onMouseLeave={handleButtonMouseLeave}
                onClick={() => handleLinkClick('home')}
              >
                Home
              </button>
            </li>
            <li>
              <button
                style={getButtonStyle('about')}
                onMouseEnter={() => handleButtonMouseEnter('about')}
                onMouseLeave={handleButtonMouseLeave}
                onClick={() => handleLinkClick('about')}
              >
                About
              </button>
            </li>
            <li>
              <button
                style={getButtonStyle('projects')}
                onMouseEnter={() => handleButtonMouseEnter('projects')}
                onMouseLeave={handleButtonMouseLeave}
                onClick={() => handleLinkClick('projects')}
              >
                Projects
              </button>
            </li>
            <li>
              <button
                style={getButtonStyle('certifications')}
                onMouseEnter={() => handleButtonMouseEnter('certifications')}
                onMouseLeave={handleButtonMouseLeave}
                onClick={() => handleLinkClick('certifications')}
              >
                Certifications
              </button>
            </li>
            <li>
              <button
                style={getButtonStyle('contact')}
                onMouseEnter={() => handleButtonMouseEnter('contact')}
                onMouseLeave={handleButtonMouseLeave}
                onClick={() => handleLinkClick('contact')}
              >
                Contact
              </button>
            </li>
          </ul>
        </nav>
      </div>
      

      
      </header>
  );
};



const imageContainerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '100px'
};

const bioimage = {
  width: '15%',
  height: '100%',
  objectFit: 'cover',
  border: '10px solid #531',
  boxShadow: '20px 20px 5px rgba(0, 0, 0, 200)',
};

const imageStyle = {
  width: '15%',
  height: '100%',
  objectFit: 'cover',
};

const imageStyle1 = {
  marginTop: '30px',
  width: '1000px',
  height: '1414px',
  margin: 'auto',
  display: 'block',
};

const bioBoxStyle = {
  marginTop: '100px',
  backgroundColor: '#222',
  textAlign: 'center',
  padding: '20px',
  boxShadow: '20px 20px 5px rgba(0, 0, 0, 200)'
};

const bioBoxStyle1 = {
  height: '100%',
  marginTop: '50px',
  backgroundColor: '#222',
  textAlign: 'center',
  padding: '20px',
  boxShadow: '20px 20px 5px rgba(0, 0, 0, 200)'
};

const headerStyle = {
  backgroundColor: '#222',
  padding: '20px'
};

const buttonContainerStyle = {
  display: 'flex',
  listStyle: 'none',
  padding: '0',
};
export default Certifications;
